import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/header';
import AddNote from './components/addNote'
import Note from './components/note'


function App() {

  useEffect(() => {
    fetch('/api/items').then(res=>res.json()).then(data=>setNotes(data))
    return () => {
    }
  }, [])

  const [notes,setNotes]=useState([]);
  const [searched,setSearched]=useState([])
  const [edit,setEdit]=useState({
    title:"",
    details:"",
    editNote:false
  })

  function handleSerach(note){
    setSearched(()=>{
      return notes.filter((noteItem)=>{
        if(noteItem.title.includes(note))
        {
          return noteItem;
        }
    }) 
  });    
  }

  let Array=searched.length>0?searched:notes;



  function handleAdd(note){
    // console.log(note);
    fetch("/api/items",{method:'post',body:({title:"asdfgh",discription:"fghj"})})
    fetch('/api/items').then(res=>res.json()).then(data=>setNotes(data))

  }

  function Delete(title){
    setNotes(prev=>{
      return prev.filter((Array,index)=>{
        return Array.title!=title
      })
    })
    setSearched(prev=>{
      return prev.filter((Array,index)=>{
        return Array.title!=title
      })
    })
  }

  function Edit(title,detail){
    setEdit({
      "title":title,
      "details":detail,
      editNote:true
    })
  }


  return (
    <div className="App">
      <Header onSearch={handleSerach}/>
      <AddNote onAdd={handleAdd} edited={edit}/>

      {

      Array.map((noteItem,index)=>{
        return(<Note 
        key={index}
        id={index}
        title={noteItem.title}
        details={noteItem.description}
        onDelete={Delete}
        onEdit={Edit}
        />)
      })

      } 

    </div>
  );
}

export default App;























